import { useEffect, useState } from "react";
// import { set } from "mongoose";


function Dashboard() {
  const navigate = useNavigate();

  

  // const [myFile,setFile] = useState(0);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  // useEffect(() => {
