import { useNavigate } from "react-router-dom";
import UploadFile from "./UploadFile";
import FileList from "./FileList";
import fetchFiles  from "./FileList"; // Adjust the import path as necessary
import { useEffect, useState } from "react";
import Navbar from "./Navbar";
// import { set } from "mongoose";


function Dashboard() {
  const navigate = useNavigate();

  

  // const [myFile,setFile] = useState(0);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  // useEffect(() => {
  //   setFile(fetchFiles());
  // }, [myFile]);

  return (
    <>  
    <div className="div">
      <Navbar />      
      <div className="container py-5">
        <div className="text-center mb-4">
          <h2 className="fw-bold text-gradient">üìÅ Welcome to your Dashboard</h2>
          <button onClick={handleLogout} className="btn btn-outline-danger mt-3">
            üö™ Logout
          </button>
        </div>


        <div className="row">
          <div className="col-md-6  mb-4">
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <UploadFile />
              </div>
            </div>
          </div>

          <div className="col-lg-6  mb-4">
            <div className="card shadow-sm h-100">
              <div className="card-body">
                <FileList />
              </div>
            </div>
          </div>
        </div>


      </div>
    </div>    
    </>

  );
}

export default Dashboard;
