import React, { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);



  const handleDelete = async (fileId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/files/delete/${fileId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFiles(); // Refresh list
    } catch (err) {
      console.error("Failed to delete file", err);
    }
  };

const handleDownload = async (fileId, originalName) => {
  try {
    const token = localStorage.getItem("token");
    const response = await axios.get(`http://localhost:5000/api/files/download/${fileId}`, {
      headers: { Authorization: `Bearer ${token}` },
      responseType: "blob", // Important: fetch file as blob
    });

    // Create a blob link to download
    const url = window.URL.createObjectURL(new Blob([response.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", originalName);
    document.body.appendChild(link);
    link.click();
    link.parentNode.removeChild(link);
  } catch (err) {
    console.error("Failed to download file", err);
  }
};


  useEffect(() => {
    fetchFiles();
  }, []);

  return (
    <div>
      <h2>Your Files</h2>
      {loading ? (
        <p>Loading files...</p>
      ) : files.length === 0 ? (
        <p>No files uploaded yet.</p>
      ) : (
        <ul>
          {files.map((file) => (
            <li key={file._id}>
              <strong>{file.originalName}</strong>
              <button onClick={() => handleDownload(file._id, file.originalName)} style={{ marginLeft: "10px" }}>
                ⬇️ Download
              </button>

              <button onClick={() => handleDelete(file._id)} style={{ marginLeft: "5px" }}>
                ❌ Delete
              </button>
              {file.uploadTime && (
                <small style={{ marginLeft: "10px", color: "#888" }}>
                  ({new Date(file.uploadTime).toLocaleString()})
                </small>
              )}
              
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default FileList;
