import { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);

  const fetchFiles = async () => {
    const token = localStorage.getItem("token");
    if (!token) return alert("Unauthorized — Please login first.");

    try {
      const response = await axios.get("http://localhost:5000/api/files/list", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setFiles(response.data.files);
    } catch (err) {
      console.error("Failed to fetch files", err);
      alert("Failed to fetch files");
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  return (
    <div>
      <h3>Uploaded Files</h3>
      {files.length === 0 ? (
        <p>No files uploaded yet.</p>
      ) : (
        <ul>
          {files.map((file) => (
            <li key={file._id}>
              <a
                href={`http://localhost:5000/uploads/${file.serverFilename}`}
                target="_blank"
                rel="noopener noreferrer"
                download
              >
                {file.originalName}
              </a>{" "}
              — Uploaded at {new Date(file.uploadTime).toLocaleString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default FileList;
