import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";  // correction: should be 'react-router-dom'

function Navbar() {
  const [darkMode, setDarkMode] = useState(false);

  // Apply/remove dark mode class on body when darkMode changes
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
      
      // document.classList.remove("homepage-bg");
    }
  }, [darkMode]);

  const handleToggle = () => {
    setDarkMode(!darkMode);
  };

  return (
    <nav className="navbar navbar-expand-lg shadow-sm" st>
      <div className="container">
        <Link className="navbar-brand fw-bold" to="/">
          ðŸ“‚ DataStorage
        </Link>

        {/* Dark Mode Toggle Switch */}
        <div className="form-check form-switch ms-3">
          <input
            className="form-check-input"
            type="checkbox"
            id="darkModeSwitch"
            checked={darkMode}
            onChange={handleToggle}
          />
          <label className="form-check-label" htmlFor="darkModeSwitch">
            ðŸŒ™
          </label>
        </div>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/login">
                Login
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/register">
                Register
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/dashboard">
                Dashboard
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
