import  { useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Button,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Switch,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import { Link } from "react-router-dom";

function Navbar({ darkMode, setDarkMode }) {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("md"));

  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };

  const drawerContent = (
    <Box sx={{ width: 250 }}>
      <List>
        {["Login", "Register", "Dashboard"].map((text) => (
          <ListItem
            button
            component={RouterLink}
            to={`/${text.toLowerCase()}`}
            key={text}
            onClick={() => setDrawerOpen(false)}
          >
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <>
      <AppBar
        position="static"
        sx={{
          background: "linear-gradient(45deg, #667eea, #764ba2)",
          transition: "all 0.5s ease-in-out",
        }}
      >
        <Toolbar>
          {/* Logo */}
          <Typography
            variant="h6"
            component={RouterLink}
            to="/"
            sx={{
              flexGrow: 1,
              textDecoration: "none",
              color: "#fff",
              fontWeight: "bold",
            }}
          >
            ðŸ“‚ DataStorage
          </Typography>

          {/* Toggle Switch */}
          <Switch
            checked={darkMode}
            onChange={() => setDarkMode(!darkMode)}
            sx={{
              "& .MuiSwitch-thumb": { backgroundColor: "#fff" },
              "& .MuiSwitch-track": { backgroundColor: "#bbb" },
            }}
          />

          {/* Desktop Links */}
          {!isMobile && (
            <Box>
              <Button
                component={RouterLink}
                to="/login"
                sx={{
                  color: "#fff",
                  mx: 1,
                  fontWeight: "bold",
                  transition: "all 0.3s ease",
                  "&:hover": { color: "#ddd" },
                }}
              >
                Login
              </Button>
              <Button
                component={RouterLink}
                to="/register"
                sx={{
                  color: "#fff",
                  mx: 1,
                  fontWeight: "bold",
                  transition: "all 0.3s ease",
                  "&:hover": { color: "#ddd" },
                }}
              >
                Register
              </Button>
              <Button
                component={RouterLink}
                to="/dashboard"
                sx={{
                  color: "#fff",
                  mx: 1,
                  fontWeight: "bold",
                  transition: "all 0.3s ease",
                  "&:hover": { color: "#ddd" },
                }}
              >
                Dashboard
              </Button>
            </Box>
          )}

          {/* Mobile Hamburger */}
          {isMobile && (
            <IconButton
              color="inherit"
              edge="end"
              onClick={handleDrawerToggle}
              sx={{ ml: 1 }}
            >
              <MenuIcon />
            </IconButton>
          )}
        </Toolbar>
      </AppBar>

      {/* Drawer */}
      <Drawer anchor="right" open={drawerOpen} onClose={handleDrawerToggle}>
        {drawerContent}
      </Drawer>
    </>
  );
}

export default Navbar;
