import React, { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);

  const fetchFiles = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/files/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFiles(res.data.files);
    } catch (err) {
      console.error("Failed to fetch files", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/files/delete/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFiles(); // Refresh list
    } catch (err) {
      console.error("Failed to delete file", err);
    }
  };

  const handleDownload = (filename) => {
    const token = localStorage.getItem("token");
    window.open(`http://localhost:5000/api/files/download/${filename}?token=${token}`, "_blank");
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  return (
    <div>
      <h2>Your Files</h2>
      {files.length === 0 ? (
        <p>No files uploaded yet.</p>
      ) : (
        <ul>
          {files.map((file) => (
            <li key={file._id}>
              {file.originalName} 
              <button onClick={() => handleDownload(file.serverFilename)}>⬇️ Download</button>
              <button onClick={() => handleDelete(file._id)}>❌ Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default FileList;
