import React, { useEffect, useState } from "react";
import axios from "axios";

function FileList() {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
const token = localStorage.getItem("token");
  const fetchFiles = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/files/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFiles(res.data.files);
    } catch (err) {
      console.error("Failed to fetch files", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (fileId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/files/delete/${fileId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFiles(); // Refresh list
    } catch (err) {
      console.error("Failed to delete file", err);
    }
  };

  const handleDownload = (fileUrl) => {
    if (fileUrl) {
      window.open(fileUrl, "_blank");
    } else {
      console.error("Invalid file URL");
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  return (
    <div>
      <h2>Your Files</h2>
      {loading ? (
        <p>Loading files...</p>
      ) : files.length === 0 ? (
        <p>No files uploaded yet.</p>
      ) : (
        <ul>
          {files.map((file) => (
            <li key={file._id}>
              <strong>{file.originalName}</strong>
              <button onClick={() => handleDownload(file.fileUrl)} style={{ marginLeft: "10px" }}>
                ⬇️ Download
              </button>
              <button onClick={() => handleDelete(file._id)} style={{ marginLeft: "5px" }}>
                ❌ Delete
              </button>
              {file.uploadTime && (
                <small style={{ marginLeft: "10px", color: "#888" }}>
                  ({new Date(file.uploadTime).toLocaleString()})
                </small>
              )}
              <a href={`http://localhost:5000/api/files/download/${file._id}?token=${token}`}>
  Download
</a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default FileList;
