import { useNavigate } from "react-router-dom";
import UploadFile from "./UploadFile";
import FileList from "./FileList";
import fetchFiles  from "./FileList"; // Adjust the import path as necessary
import { useEffect, useState } from "react";
// import { set } from "mongoose";


function Dashboard() {
  const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

    const fetchFiles = async () => {
    try {
      const token = localStorage.getItem("token");
      // console.log("Token being sent:", token);
      const res = await axios.get("http://localhost:5000/api/files/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      // console.log("Files fetched:", res.data.files);
      setFiles(res.data.files);
    } catch (err) {
      console.error("Failed to fetch files", err);
    } finally {
      setLoading(false);
    }
  };

  // const [myFile,setFile] = useState(0);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  // useEffect(() => {
  //   setFile(fetchFiles());
  // }, [myFile]);

  return (
    <div>
      <h2>Welcome to your Dashboard</h2>
      <button onClick={handleLogout}>Logout</button>
      <UploadFile />
      <FileList />
      <div>
        <h3>Your Files</h3>
        {/* This section can be expanded to list files uploaded by the user */}
        <p>File listing functionality will be implemented here.</p>
        </div>
    </div>
  );
}

export default Dashboard;
