import { useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import axios from "axios";
import UploadFile from "./UploadFile";
// import FileList from "./FileList";

function Dashboard() {
  const navigate = useNavigate();

    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);

    const fetchFiles = async () => {
      try {
        const token = localStorage.getItem("token");
        // console.log("Token being sent:", token);
        const res = await axios.get("http://localhost:5000/api/files/list", {
          headers: { Authorization: `Bearer ${token}` },
        });
        // console.log("Files fetched:", res.data.files);
        setFiles(res.data.files);
      } catch (err) {
        console.error("Failed to fetch files", err);
      } finally {
        setLoading(false);
      }
    };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <div>
      <h2>Welcome to your Dashboard</h2>
      <button onClick={handleLogout}>Logout</button>
      <UploadFile />
      <FileList />
      <div>
        <h3>Your Files</h3>
        {/* This section can be expanded to list files uploaded by the user */}
        <p>File listing functionality will be implemented here.</p>
        </div>
    </div>
  );
}

export default Dashboard;
